(function() {
  var Neat, VOWS, find, fs, run;

  fs = require('fs');

  Neat = require('../../../neat');

  run = require('../../../utils/commands').run;

  find = require('../../../utils/files').find;

  VOWS = "" + Neat.neatRoot + "/node_modules/.bin/vows";

  module.exports = function(config) {
    return;
    return config.engines.tests.vows = function(callback) {
      var msg;
      if (!fs.existsSync(VOWS)) {
        msg = "" + (red("Can't find vows module")) + "\n\nRun " + (yellow('neat install')) + " to install the dependencies.";
        return typeof callback === "function" ? callback(new Error(msg)) : void 0;
      }
      return find('coffee', ["" + Neat.root + "/test/vows"], function(err, files) {
        return run(VOWS, files, callback);
      });
    };
  };

}).call(this);
